    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        Produtos produtos = new Produtos();
        produtos.setNome(this.txtNome.getText());
        produtos.setNome(this.txtPeso.getText());
        produtos.setNome(this.txtValor.getText());
        produtos.setNome(this.txtEstoque.getText());
        produtos.setNome(this.txtMargem.getText());
        
        DefaultTableModel dtmProdutos = (DefaultTableModel) jtProdutos.getModel();
        Object[] dados = {txtNome.getText(), txtPeso.getText(), txtValor.getText(), txtEstoque.getText(),txtMargem.getText()};
        dtmProdutos.addRow(dados);
        txtNome.setText(null);
        txtPeso.setText(null);
        txtValor.setText(null);
        txtEstoque.setText(null);
        txtMargem.setText(null);
    }//GEN-LAST:event_btnCadastrarActionPerformed

